INSERT [dbo].[ALBUM] ([ALBUM_ID], [VERSION], [GENRE_ID], [ARTIST_ID], [TITLE], [DESCRIPTION], [PRICE], [ALBUM_ART_URL]) VALUES (N'd20995d7-6c99-4373-bc4f-a58200ef113f', 1, N'a67cb105-918d-47b2-9731-a58200b4e63c', N'85e8907c-7ab9-4bd5-bc16-a58200daf3d8', N'Ace of Spades', N'<strong>Ace of Spades</strong> is the fourth album by the British heavy metal band Motörhead. Released on 8 November 1980, it peaked at No. 4 on the UK album charts and reached Gold status by March 1981. It was preceded by the release of the title track as a single on 27 October, which peaked in the UK singles chart at No. 15 in early November. It was the band''s debut release in North America, with Mercury Records handling distribution in the region.', CAST(8.3900 AS Decimal(19, 4)), N'/Content/Images/Products/motorhead_lg_.jpg')
INSERT [dbo].[ALBUM] ([ALBUM_ID], [VERSION], [GENRE_ID], [ARTIST_ID], [TITLE], [DESCRIPTION], [PRICE], [ALBUM_ART_URL]) VALUES (N'cb880bb8-51d2-4215-b6d5-a58200f4f58f', 1, N'a67cb105-918d-47b2-9731-a58200b4e63c', N'41fa797f-589f-47dd-8b9e-a58200dae6a0', N'Dr. Feelgood', N'<strong>Dr. Feelgood</strong> is the fifth studio album by the American heavy metal band Mötley Crüe, released on September 1, 1989. Dr. Feelgood topped the Billboard 200 chart and was the first album Mötley Crüe recorded after their quest for sobriety and rehabilitation in 1989. In addition to being Mötley Crüe''s best selling album, it is highly regarded by music critics and fans as the band''s best studio album.', CAST(7.9900 AS Decimal(19, 4)), N'/Content/Images/Products/motleycrue_lg_.png')

